        Main.Achievements.Save();
        Player.InternalSaveMap(playerFile.IsCloudSave);
        if (Main.ServerSideCharacter)
          return;
        FileUtilities.ProtectedInvoke((Action) (() => Player.InternalSavePlayerFile(playerFile)));
      }
      catch (Exception ex)
      {
